import React, { Component } from 'react';
import axios from 'axios';
import { apiBaseUrl } from '../services/api';
import Cookies from 'js-cookie';


const payload = Cookies.get('auth');

class Integers extends Component {
  state = {
    integer: null,
    value: ''
  };

  componentWillMount() {
    this.getInteger();
  }

  getApi = () => {
    axios.get(apiBaseUrl)
      .then(response => {
        if(response.status === 200) {
          console.log(response.data);
        }
      })
      .catch(err => {
        console.log(err);
      })
  }

  // Don't have user yet.
  getInteger = () => { 
    console.log(payload);
    axios.get(apiBaseUrl + '/current', {
      headers: {
        authorization: payload
      }
    })
    .then(response => {
      if(response.status === 200) {
        console.log(response.data);
        this.setState({
          integer: response.data.integer
        })
      }
    })
    .catch(err => {
      console.log(err);
    })
  }

  displayInteger = () => {
    const data = this.state.integer;
    console.log(data);
    if(data) {
      return (
        <div>{data}</div>
      )
    }
  }

  incrementInteger = () => {
    axios.get(apiBaseUrl + '/next', {
      headers: {
        authorization: payload
      }
    })
    .then(response => {
      console.log(response.data);
    })
    .catch(err => {
      console.log(err);
    })
    this.getInteger();
  }

  handleChange = (event) => {
    this.setState({value: event.target.value});
  }

  setInteger = (int) => {

    axios.put(apiBaseUrl + '/current', )
  }

  render() {
    return (
      <div>
        <div>{this.displayInteger()}</div>
        <button onClick={() => this.incrementInteger()}>Next Integer</button>
        <input type="text" value={this.state.value} onChange={this.handleChange} />
      </div>
    )
  }
}

export default Integers;
